  styleUrls: ['./ocr-page.component.css']
})


export class OcrPageComponent {
  title = 'ocrAngular';
  Result = 'Recognizing...';
  constructor() {    
     this. test();     
  }
  test(){
    Tesseract.recognize('assets/Images/TestDemo.png').then(function(result){         
      alert(result.text);      
      });    
  }  
}
